import React, { useState, useEffect } from "react";
import create from "zustand";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { addDays, addWeeks, addMonths, addYears, isBefore } from "date-fns";

// Zustand store for state management
const useRecurringStore = create((set) => ({
  frequency: "daily",
  interval: 1,
  weekdays: [],
  monthPattern: { week: 1, day: "Monday" },
  startDate: new Date(),
  endDate: null,
  setFrequency: (frequency) => set({ frequency }),
  setInterval: (interval) => set({ interval }),
  setWeekdays: (weekdays) => set({ weekdays }),
  setMonthPattern: (monthPattern) => set({ monthPattern }),
  setStartDate: (startDate) => set({ startDate }),
  setEndDate: (endDate) => set({ endDate }),
}));

// Days for weekly selection
const weekdaysOptions = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];

const RecurringDatePicker = ({ onSubmit }) => {
  const {
    frequency,
    interval,
    weekdays,
    monthPattern,
    startDate,
    endDate,
    setFrequency,
    setInterval,
    setWeekdays,
    setMonthPattern,
    setStartDate,
    setEndDate,
  } = useRecurringStore();

  const [previewDates, setPreviewDates] = useState([]);

  const handleWeekdaysChange = (day) => {
    setWeekdays(
      weekdays.includes(day)
        ? weekdays.filter((d) => d !== day)
        : [...weekdays, day]
    );
  };

  const generateDates = () => {
    let dates = [];
    let current = startDate;
    const maxIterations = 50;

    for (let i = 0; i < maxIterations; i++) {
      if (endDate && isBefore(endDate, current)) break;

      if (frequency === "daily") {
        dates.push(current);
        current = addDays(current, interval);
      }

      if (frequency === "weekly") {
        weekdays.forEach((day) => {
          const date = addDays(current, (weekdaysOptions.indexOf(day) - current.getDay() + 7) % 7);
          if (!dates.includes(date) && (!endDate || isBefore(date, endDate))) {
            dates.push(date);
          }
        });
        current = addWeeks(current, interval);
      }

      if (frequency === "monthly") {
        dates.push(current);
        current = addMonths(current, interval);
      }

      if (frequency === "yearly") {
        dates.push(current);
        current = addYears(current, interval);
      }
    }
    dates = dates.sort((a, b) => a - b);
    setPreviewDates(dates);
    return dates;
  };

  useEffect(() => {
    generateDates();
  }, [frequency, interval, weekdays, monthPattern, startDate, endDate]);

  return (
    <div className="p-6 max-w-lg mx-auto bg-white rounded-xl shadow space-y-4">
      <h2 className="text-xl font-semibold">Recurring Date Picker</h2>

      <div className="space-y-2">
        <label className="block font-medium">Frequency</label>
        <select
          className="w-full border p-2 rounded"
          value={frequency}
          onChange={(e) => setFrequency(e.target.value)}
        >
          <option value="daily">Daily</option>
          <option value="weekly">Weekly</option>
          <option value="monthly">Monthly</option>
          <option value="yearly">Yearly</option>
        </select>
      </div>

      <div className="space-y-2">
        <label className="block font-medium">Repeat Every</label>
        <input
          type="number"
          min="1"
          className="w-full border p-2 rounded"
          value={interval}
          onChange={(e) => setInterval(Number(e.target.value))}
        />
      </div>

      {frequency === "weekly" && (
        <div className="space-y-2">
          <label className="block font-medium">Days of the Week</label>
          <div className="flex flex-wrap gap-2">
            {weekdaysOptions.map((day) => (
              <button
                key={day}
                onClick={() => handleWeekdaysChange(day)}
                className={`px-3 py-1 rounded border ${
                  weekdays.includes(day)
                    ? "bg-blue-500 text-white"
                    : "bg-gray-100"
                }`}
              >
                {day.slice(0, 3)}
              </button>
            ))}
          </div>
        </div>
      )}

      <div className="space-y-2">
        <label className="block font-medium">Start Date</label>
        <DatePicker
          selected={startDate}
          onChange={(date) => setStartDate(date)}
          className="w-full border p-2 rounded"
        />
      </div>

      <div className="space-y-2">
        <label className="block font-medium">End Date (optional)</label>
        <DatePicker
          selected={endDate}
          onChange={(date) => setEndDate(date)}
          isClearable
          className="w-full border p-2 rounded"
        />
      </div>

      <button
        className="w-full bg-blue-600 text-white py-2 rounded"
        onClick={() => onSubmit(generateDates())}
      >
        Confirm Recurrence
      </button>

      <div>
        <h4 className="font-medium mt-4">Preview Dates:</h4>
        <ul className="text-sm text-gray-600 max-h-32 overflow-y-auto">
          {previewDates.map((date, index) => (
            <li key={index}>{date.toDateString()}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default RecurringDatePicker;
